from __future__ import annotations

import json
from datetime import date
from decimal import Decimal

import httpx
import pytest

from expense_bot.graph.state import AccountMatch, ConversationState, ExpenseDraft
from expense_bot.integrations.erpnext import ERPNextClient


@pytest.fixture
def anyio_backend() -> str:
    """Limit anyio-backed tests to asyncio for httpx transports."""

    return "asyncio"


class MessengerProbe:
    """In-memory messenger that captures confirmation payloads."""

    def __init__(self) -> None:
        self.messages: list[dict[str, str | None]] = []

    async def send_posted_notice(
        self, *, thread_id: str, document_id: str, document_link: str | None
    ) -> None:
        self.messages.append(
            {
                "thread_id": thread_id,
                "document_id": document_id,
                "document_link": document_link,
            }
        )


class LangGraphPostingProbe:
    """Minimal stand-in for the LangGraph posting node."""

    def __init__(
        self,
        *,
        client: ERPNextClient,
        messenger: MessengerProbe,
    ) -> None:
        self._client = client
        self._messenger = messenger

    async def execute(self, state: ConversationState) -> None:
        draft = state.expense_draft
        if draft is None:
            raise AssertionError("ConversationState is missing an expense draft.")

        payload = {
            "posting_date": draft.posting_date.isoformat(),
            "user_remark": draft.narration,
            "accounts": [
                {
                    "account": draft.debit_account.account_code,
                    "debit_in_account_currency": float(draft.amount),
                    "credit_in_account_currency": 0.0,
                },
                {
                    "account": draft.credit_account.account_code,
                    "debit_in_account_currency": 0.0,
                    "credit_in_account_currency": float(draft.amount),
                },
            ],
        }

        result = await self._client.post_journal_entry(payload)
        state.erpnext_submission = result
        await self._messenger.send_posted_notice(
            thread_id=state.thread_id,
            document_id=result.journal_entry_id,
            document_link=result.link,
        )


def _build_state() -> ConversationState:
    draft = ExpenseDraft(
        amount=Decimal("10.00"),
        currency="USD",
        debit_account=AccountMatch(
            account_code="5110",
            display_name="Taxi Expense - HQ",
            confidence=0.99,
        ),
        credit_account=AccountMatch(
            account_code="1000",
            display_name="Cash - HQ",
            confidence=0.98,
        ),
        posting_date=date(2025, 11, 7),
        narration="Paid $10 cash for taxi",
    )
    return ConversationState(thread_id="chat:481516", expense_draft=draft)


@pytest.mark.anyio("asyncio")
async def test_document_reference_flows_from_erpnext_to_messenger() -> None:
    """The LangGraph posting flow must echo ERPNext doc IDs back to Telegram."""

    captured_payload: dict[str, object] = {}

    def handler(request: httpx.Request) -> httpx.Response:
        captured_payload.update(json.loads(request.content.decode("utf-8")))
        return httpx.Response(
            200,
            json={
                "data": {
                    "name": "JE-4242",
                    "posting_date": "2025-11-07",
                    "voucher_no": "VCH-4242",
                    "link": "https://erp.test/app/journal-entry/JE-4242",
                }
            },
        )

    transport = httpx.MockTransport(handler)
    messenger = MessengerProbe()
    state = _build_state()

    async with httpx.AsyncClient(
        transport=transport,
        base_url="https://erp.test",
    ) as http_client:
        client = ERPNextClient(
            base_url="https://erp.test",
            api_key="key-123",
            api_secret="secret-456",
            default_company="Cuenta HQ",
            http_client=http_client,
        )
        flow = LangGraphPostingProbe(client=client, messenger=messenger)
        await flow.execute(state)

    assert captured_payload["accounts"][0]["account"] == "5110"
    assert state.erpnext_submission is not None, "ERPNext submission metadata missing."
    assert state.erpnext_submission.journal_entry_id == "JE-4242"
    assert messenger.messages == [
        {
            "thread_id": "chat:481516",
            "document_id": "JE-4242",
            "document_link": "https://erp.test/app/journal-entry/JE-4242",
        }
    ]
